import{_ as l,c as i,o as t,d as e}from"./app.85131896.js";const r="/MyWeb/assets/image-20230227185221968.e914871d.png",s="/MyWeb/assets/image-20230227210453448.a77bd1bb.png",o="/MyWeb/assets/image-20230227210623290.2ff12ad7.png",n="/MyWeb/assets/image-20230227192242999.ef008227.png",a="/MyWeb/assets/image-20230227205501935.e86c7d95.png",p="/MyWeb/assets/image-20230306185359524.a72df283.png",g="/MyWeb/assets/image-20230306192527448.c5ec8ed5.png",u="/MyWeb/assets/image-20230313190513192.e287facc.png",c="/MyWeb/assets/image-20230313190605095.1e49f812.png",d="/MyWeb/assets/image-20230313203307160.44b8df09.png",h="/MyWeb/assets/image-20230313205806923.c929a3a1.png",m="/MyWeb/assets/image-20230313211254362.9d2776d9.png",_="/MyWeb/assets/image-20230313211311946.acfa55bf.png",b="/MyWeb/assets/image-20230313211346807.4e8f4306.png",C=JSON.parse('{"title":"现代管理基础笔记","description":"","frontmatter":{},"headers":[{"level":2,"title":"Lecture 1","slug":"lecture-1","link":"#lecture-1","children":[{"level":3,"title":"过程考核：","slug":"过程考核","link":"#过程考核","children":[]},{"level":3,"title":"现代管理的特征","slug":"现代管理的特征","link":"#现代管理的特征","children":[]}]},{"level":2,"title":"Lecture 2：管理及其有效管理chapter 1","slug":"lecture-2-管理及其有效管理chapter-1","link":"#lecture-2-管理及其有效管理chapter-1","children":[{"level":3,"title":"chapter 1 管理导论","slug":"chapter-1-管理导论","link":"#chapter-1-管理导论","children":[]}]},{"level":2,"title":"Lecture 2 有效管理","slug":"lecture-2-有效管理","link":"#lecture-2-有效管理","children":[{"level":3,"title":"衡量管理有效性","slug":"衡量管理有效性","link":"#衡量管理有效性","children":[]}]}],"relativePath":"note/TS/现代管理基础Note.md"}'),f={name:"note/TS/现代管理基础Note.md"},k=e('<h1 id="现代管理基础笔记" tabindex="-1">现代管理基础笔记 <a class="header-anchor" href="#现代管理基础笔记" aria-hidden="true">#</a></h1><h2 id="lecture-1" tabindex="-1">Lecture 1 <a class="header-anchor" href="#lecture-1" aria-hidden="true">#</a></h2><p><img src="'+r+'" alt="image-20230227185221968"></p><h3 id="过程考核" tabindex="-1">过程考核： <a class="header-anchor" href="#过程考核" aria-hidden="true">#</a></h3><ul><li>线上知识点测试：40pts</li><li>线上个人作业及互评：10pts <ul><li>800字心得体会</li><li>计划管理 <ul><li>周计划</li><li>落实</li><li>三点体会</li></ul></li></ul></li><li>线下小组作业：30pts <ul><li>身上的管理问题</li><li>PPT</li><li>组队方式？</li><li>最后一周分享</li></ul></li><li>课上发言+随机提问：10pts <ul><li>课上发言</li><li>网上发言</li></ul></li></ul><p><img src="'+s+'" alt="image-20230227210453448"></p><p><img src="'+o+'" alt="image-20230227210623290"></p><p><strong>注意上课时间为晚上</strong></p><h3 id="现代管理的特征" tabindex="-1">现代管理的特征 <a class="header-anchor" href="#现代管理的特征" aria-hidden="true">#</a></h3><p><img src="'+n+'" alt="image-20230227192242999"></p><ol><li><p>结构化的分析：</p><ul><li><p>基本信息</p></li><li><p>行为信息</p></li><li><p>信用信息</p></li><li><p>需求信息</p></li><li><p>...</p></li></ul></li><li><p>可视化的呈现</p></li><li><p>目的</p><ul><li>客制化服务</li></ul></li></ol><h4 id="管理环境发生变化" tabindex="-1">管理环境发生变化 <a class="header-anchor" href="#管理环境发生变化" aria-hidden="true">#</a></h4><ul><li>黑天鹅：不可预知的未来事件</li><li>灰犀牛：大概率发生的恶性事件</li></ul><h4 id="时代背景" tabindex="-1"><strong>时代背景</strong> <a class="header-anchor" href="#时代背景" aria-hidden="true">#</a></h4><ul><li>VUCA(乌卡)时代 <ul><li>变动的</li><li>不确定的</li><li>复杂的</li><li>模糊的</li></ul></li><li>RUPT时代 <ul><li>急速的</li><li>莫测的</li><li>矛盾的</li><li>复杂的</li></ul></li></ul><h4 id="管层面的新思考" tabindex="-1"><strong>管层面的新思考</strong> <a class="header-anchor" href="#管层面的新思考" aria-hidden="true">#</a></h4><ul><li>环境洞察：Environment Insight</li><li>情景规划：Scenario Planning <ul><li>对不同情景的方案</li></ul></li><li>快速反应：Rapid Response</li><li>组织弹性：Company Flexibility <ul><li>组织韧性</li></ul></li><li>跨界合作：Cross-border Cooperation</li><li>心智锤炼：Mental Training</li><li>舆情管控：Public opinion control</li><li>生态圈层</li><li>有效复盘</li></ul><p>=&gt; 十倍速变化</p><p><strong>管理对象</strong></p><ul><li>自我管理：self-management <ul><li>情绪</li><li>物品</li><li>时间</li><li>空间</li><li>...</li></ul></li><li>团队管理</li><li>组织管理</li><li>社会管理</li><li>生态管理</li></ul><p><strong>管理过程</strong>：4+1</p><ul><li>计划</li><li>组织：人、财、物</li><li>领导：领导的对象是人，要加以控制</li><li>控制：把握、落实、施行标准</li><li>创新 <ul><li>迭代</li><li>颠覆</li></ul></li></ul><p><strong>管理者</strong></p><ul><li>素质要求</li><li>基本职责</li><li>发展通道</li></ul><p><img src="'+a+'" alt="image-20230227205501935"></p><p>管理要回答的三个问题：</p><ul><li><p>目标</p></li><li><p>路径</p><ul><li>多个备选方案</li></ul></li><li><p>“施工”方案</p><ul><li>领导</li><li>控制</li></ul></li></ul><p><a href="https://www.amazon.cn/dp/B085C9Q31P" target="_blank" rel="noreferrer">https://www.amazon.cn/dp/B085C9Q31P</a></p><p><img src="https://images-cn.ssl-images-amazon.cn/images/I/31Ik4Mcdb8L.jpg" alt="“现代管理基础（第二版）”，作者：[周亚庆]"></p><p><img src="'+p+'" alt="image-20230306185359524"></p><h2 id="lecture-2-管理及其有效管理chapter-1" tabindex="-1">Lecture 2：管理及其有效管理chapter 1 <a class="header-anchor" href="#lecture-2-管理及其有效管理chapter-1" aria-hidden="true">#</a></h2><h3 id="chapter-1-管理导论" tabindex="-1">chapter 1 管理导论 <a class="header-anchor" href="#chapter-1-管理导论" aria-hidden="true">#</a></h3><h4 id="第一节-管理的内涵" tabindex="-1">第一节 管理的内涵 <a class="header-anchor" href="#第一节-管理的内涵" aria-hidden="true">#</a></h4><ul><li><p><strong>传统定义</strong></p><ol><li>德鲁克（现代管理之父）：管理是<strong>谋取剩余</strong>（<em>产出大于投入的部分</em>）</li></ol><blockquote><p><strong>精益管理</strong>：旨在需要的时候，按需要的量，生产所需的产品。</p><ul><li>库存控制</li><li>生产计划</li><li>成本管理</li><li>供应链</li><li>...</li></ul><p><strong>品牌依恋/品牌忠诚</strong></p><p><strong>商业模式</strong></p></blockquote><ol start="2"><li>西蒙（决策管理大师）：管理就是<strong>决策</strong></li></ol><blockquote><p>简单决策/复杂决策</p><p>个人决策/群体决策</p></blockquote><ol start="3"><li>穆尼：管理就是<strong>领导</strong></li></ol><blockquote><p>关注领导的<strong>有效性</strong></p><p>领导的面向是人，管理的面向可以是其他东西</p><p>领导往往与重大事项联系在一起，管理依托职位的权力</p></blockquote><ol start="4"><li>法约尔（经营管理之父）：管理就是计划、组织、指挥、协调和<strong>控制</strong></li></ol><blockquote><p><strong>多角度思考：立场、方法、角度不一样</strong></p><p>在一定环境下对组织内外资源包括人力资源的调配</p></blockquote><ol start="5"><li>孔茨：管理是借助他人达成目标的过程</li></ol></li><li><p><strong>本书定义：</strong></p><p>​ 管理是指人们根据组织的内外部<strong>环境</strong>，整合人、财、物、信息和时间等<strong>资源</strong>，通过==<em><strong>计划、组织、领导、控制与创新</strong></em>==等<strong>职能</strong>，以有效<strong>实现预定目标</strong>的过程。</p></li><li><p><strong>管理要点：</strong></p><ol><li><strong>管理的工作</strong>必须基于特定环境。管理工作需要不断适应环境。</li><li><strong>管理的对象</strong>是组织的资源。<em><strong>人</strong>是生产力中最根本的、最活跃的因素</em>，人具有主观能动性。</li><li><strong>管理的基本职能</strong>是<em>计划、组织、领导、控制与创新</em>。</li><li><strong>管理的核心</strong>是<strong>协调与整合</strong>。</li><li><strong>管理的目标</strong>是高效地实现预定<strong>目标</strong>。</li></ol><blockquote><p><strong>美好的愿景</strong>是团队合作的基石；</p><p><strong>明确的目标</strong>是团队成功的关键；</p><p><strong>团结协作</strong>是团队合作的关键。</p><p>经验也是一种生产力，通过在团体中的共享，可以产生意想不到的效果。</p></blockquote></li><li><p><strong>管理的实质</strong></p><p><strong>协商是管理的本质</strong>，管理是手段而不是目的。人类活动的显著特征之一就是具有明确的目的性。</p><ul><li>管理本身不是目的</li><li>管理是一种工具</li></ul></li></ul><h4 id="第二节-管理思想的演变" tabindex="-1">第二节 管理思想的演变 <a class="header-anchor" href="#第二节-管理思想的演变" aria-hidden="true">#</a></h4><blockquote><p>管理考虑：管理者感受、管理成本</p></blockquote><ul><li>管理产生的原因</li></ul><p><img src="'+g+'" alt="image-20230306192527448"></p><p>资源的有限性：人员、时间、媒介、空间...</p><p><strong>=&gt;资源有限性与欲望无限性的矛盾普遍存在</strong></p><ol><li>生产：通过<strong>劳动</strong>向大自然索要资源，从而在一定程度上满足自身欲望。</li><li>贸易：通过自身多余或价值不大的资源与他人<strong>交换</strong>，获得自身稀缺的资源，以更好地满足自身的欲望。</li><li>组织：通过与他人的<strong>分工协助</strong>，形成超越个人的群体力量，从而达到靠个人的力量无法实现的欲望。</li><li>道德：通过<strong>教育约束</strong>每一个人的欲望，使有限的资源足以满足众人的欲望。</li><li>战争：通过<strong>掠夺他人资源</strong>来扩展自身资源，从而满足自己的欲望。</li><li>管理：通过<strong>科学</strong>的方法提高资源的利用率，力求以有限的资源实现尽可能多的欲望。</li></ol><ul><li><p>管理思想的发展</p><ol><li><p>早期管理</p></li><li><p>经验管理</p></li><li><p>科学管理</p><ol><li><p>科学管理理论</p><p>四条原则</p><table><thead><tr><th>原则</th><th>内容</th></tr></thead><tbody><tr><td>科学的方法</td><td>对工作的每个要素开发出科学的方法，以代替陈旧的经验方法</td></tr><tr><td>合适的工人</td><td>科学地挑选，培训与教导工人，使之成长</td></tr><tr><td>真诚的合作</td><td>管理者真诚与工人进行合作，以确保工人按照科学的方法形式</td></tr><tr><td>明确的职责</td><td>管理者：按照科学原理指定工作方法 ；工人负责按此完成相应的工作</td></tr></tbody></table><p>科学管理是所有相关要素的整合</p></li><li><p>一般管理理论</p></li><li><p>官僚组织理论</p></li></ol></li><li><p>行为管理思想</p><ul><li><p>将重点放在影响组织中个体行为的各种因素上，强调管理的重点是理解人的行为</p></li><li><p>基本观点：人不仅是经济人，更是社会人，其劳动生产率受社会、心理和群体因素影响</p></li></ul><ol><li>霍桑实验 <ul><li>工人是“社会人”，是复杂的社会系统成员，人们的行为不单纯出于追求金钱的动机，还有社会、心理方面的需求。领导者若能真诚关心下属，重视感情沟通，那么工人的生产率将会有较大的提高。</li><li>企业在除了正式组织还存在非正式组织。非正式组织以其特有的感情倾向与精神导向，左右着成员的行为。</li><li>新的领导能力在于提高工人的满意度。工人的高满意度来源于其个人需求的有效满足，既包括物质需求，也包括精神需求。</li></ul></li><li>人际关系运动 <ul><li>目的是提高人际技能与克服沟通障碍。</li></ul></li><li>行为科学学派 <ul><li>强调通过科学研究来形成关于组织中的人的行为理论，并据此指导管理者的管理实践。</li></ul></li></ol></li><li><p>定量管理思想</p><ul><li>有效管理的关键在于度的把握，定量化能提高管理的效率与效益。定量管理思想的核心是把数学、统计学和信息技术用于管理决策与提高。</li></ul><ol><li>管理科学</li><li>作业管理</li><li>信息技术在管理中的应用</li></ol></li><li><p>权变管理思想</p><ol><li>系统理论 <ul><li>管理者的作用就是确保组织中的各个部分之间的相互协调，以实现组织的整体目标。</li></ul></li><li>权变理论 <ul><li>组织的管理应根据其所处在内外部环境的变化而变化。</li></ul></li><li>过程理论 <ul><li>管理是一个过程，认为管理由许多相互关联的职能所组成</li></ul></li></ol></li></ol></li></ul><h4 id="第三节-管理的重要性" tabindex="-1">第三节：管理的重要性 <a class="header-anchor" href="#第三节-管理的重要性" aria-hidden="true">#</a></h4><h4 id="第四节-管理学及其特点" tabindex="-1">第四节：管理学及其特点 <a class="header-anchor" href="#第四节-管理学及其特点" aria-hidden="true">#</a></h4><p>管理的特点</p><ul><li>管理学是一门不精确的科学</li><li>管理学是一门综合性的科学</li><li>管理学是一门应用性的科学</li></ul><p>管理学是一门科学，管理实践则具有艺术性。管理的精髓是“理”不是“管”</p><h2 id="lecture-2-有效管理" tabindex="-1">Lecture 2 有效管理 <a class="header-anchor" href="#lecture-2-有效管理" aria-hidden="true">#</a></h2><p><img src="'+u+'" alt="image-20230313190513192"></p><p><img src="'+c+'" alt="image-20230313190605095"></p><p>全面理解管理：</p><ul><li><p>管理必须基于特定的<strong>环境</strong></p></li><li><p>管理的<strong>对象</strong>是组织的<strong>资源</strong>（包括人力资源）</p></li><li><p>管理的基本职能是<strong>计划、组织、领导、控制和创新</strong></p></li><li><p>管理的核心是<strong>协调</strong>和<strong>整合</strong></p><ul><li>协调：使多个看似矛盾的事物（如长远目标与短期目标、有限资源和无限欲望）之间的有机结合、同步和谐</li><li>形式： <ul><li>协调工作中的问题</li><li>协调工作间的问题</li><li>协调活动与目标之间的关系</li></ul></li></ul></li><li><p>管理的目的是<strong>有效实现既定目标</strong></p></li><li><p>管理的实质是人们为了<strong>实现一定的目标</strong>而采取的一种手段</p></li><li><p>管理的功能是以<strong>有限的资源</strong>实现<strong>更多的目标</strong></p></li><li><p>管理的道德</p><ul><li>商业向善</li></ul></li></ul><p>管理的基础和依据：法理/道理/情理</p><p>管理的方法：整理/治理/调理</p><p>管理的结果：合理/有理</p><p><strong>管理好坏如何区分和衡量</strong></p><ul><li>投入产出比</li><li>可持续发展</li><li>目标达成</li><li>文化</li><li>流动性</li><li>规模</li><li>客户满意度/客户留存率</li><li>员工满意度/领导满意度</li></ul><h3 id="衡量管理有效性" tabindex="-1">衡量管理有效性 <a class="header-anchor" href="#衡量管理有效性" aria-hidden="true">#</a></h3><p>有效性指标：</p><ul><li><strong>效率：投入产出比</strong>=&gt; 怎么做：以比较经济的方法</li><li><strong>效益：目标达成度</strong>=&gt; 做什么：做有利于<strong>目标</strong>实现的事情</li></ul><p><em>有效的管理：<strong>做好对的事</strong>，既要讲究效率又要讲究效益</em> To do right thing right</p><p><strong>效益是优先的。其次再考虑方法。</strong></p><p>德鲁克：有效管理的五项原则</p><ol><li>知道时间用在哪里</li><li>重视外部贡献</li><li>善于利用各人的长处</li><li>集中精力于重要领域</li><li>善于做出有效的决策</li></ol><img src="'+d+'" alt="image-20230313203307160" style="zoom:50%;"><blockquote><p>创新和决策贯穿管理的全过程</p></blockquote><ul><li><p>计划：</p><ul><li>计划是管理的首要职责</li><li>解决合理配置资源的问题</li><li>有效计划特征： <ul><li>目的性</li><li>预见性</li><li>稳定性：相对稳定</li><li>灵活性</li></ul></li><li></li></ul></li><li><p>组织</p><ul><li>组织工作是为了有效达成计划所确定的目标而进行组织结构的设计与变革、人员配备以及权力配置的过程</li><li>关注内外资源的整合利用：比如不同领域间企业的合作</li><li>要求： <ul><li>合理性</li><li>效益性</li><li>规范性</li><li>动态性</li></ul></li></ul></li><li><p>领导</p><ul><li>权力配置</li><li>领导职能更受管理者素质和管理风格的影响，是一件具有很强<strong>艺术性</strong>的管理活动。</li></ul><img src="'+h+'" alt="image-20230313205806923" style="zoom:50%;"></li><li><p>控制</p><ul><li>检查、评估、纠正</li><li>定标准，测成效，纠偏差</li></ul></li><li><p>创新</p><ul><li>五种创新 <ul><li>采用一种新产品</li><li>采用一种新生产方法</li><li>开辟一个新市场</li><li>活动原材料或半成品的一种新供应来源</li><li>实现任何一种产业的新组织</li></ul></li></ul></li></ul><img src="'+m+'" alt="image-20230313211254362" style="zoom:50%;"><img src="'+_+'" alt="image-20230313211311946" style="zoom:50%;"><img src="'+b+'" alt="image-20230313211346807" style="zoom:50%;">',70),y=[k];function x(M,q,W,T,v,P){return t(),i("div",null,y)}const S=l(f,[["render",x]]);export{C as __pageData,S as default};
