import{_ as l,o as i,c as t,O as o}from"./chunks/framework.46f3d25f.js";const a="/MyWeb/assets/image-20230227185221968.e914871d.png",e="/MyWeb/assets/image-20230227210453448.a77bd1bb.png",s="/MyWeb/assets/image-20230227210623290.2ff12ad7.png",r="/MyWeb/assets/image-20230227192242999.ef008227.png",p="/MyWeb/assets/image-20230227205501935.e86c7d95.png",n="/MyWeb/assets/image-20230306185359524.a72df283.png",g="/MyWeb/assets/image-20230306192527448.c5ec8ed5.png",u="/MyWeb/assets/image-20230313190513192.e287facc.png",c="/MyWeb/assets/image-20230313190605095.1e49f812.png",m="/MyWeb/assets/image-20230313203307160.44b8df09.png",h="/MyWeb/assets/image-20230313205806923.c929a3a1.png",d="/MyWeb/assets/image-20230313211254362.9d2776d9.png",b="/MyWeb/assets/image-20230313211311946.acfa55bf.png",_="/MyWeb/assets/image-20230313211346807.4e8f4306.png",q="/MyWeb/assets/image-20230320194731091.fb62d812.png",f="/MyWeb/assets/image-20230320194846753.7b887b8e.png",k="/MyWeb/assets/image-20230320195524123.d741923e.png",y="/MyWeb/assets/image-20230320201922023.386a5099.png",P="/MyWeb/assets/image-20230320202319069.baeb91ce.png",x="/MyWeb/assets/image-20230320210344918.21df3de1.png",M="/MyWeb/assets/image-20230320211521261.074dcdec.png",W="/MyWeb/assets/image-20230327190512925.22aeccf3.png",z="/MyWeb/assets/image-20230327190541921.a1cd12fb.png",T="/MyWeb/assets/image-20230327190623061.8925e331.png",L="/MyWeb/assets/image-20230327190342005.b39e07a3.png",C="/MyWeb/assets/image-20230327201730639.e0e1528c.png",S="/MyWeb/assets/image-20230327204335171.0fcfb56f.png",N="/MyWeb/assets/image-20230327194719243.69aa0dde.png",w="/MyWeb/assets/image-20230327200844740.074508dc.png",I="/MyWeb/assets/image-20230327201402661.1eef6110.png",A="/MyWeb/assets/image-20230327201608482.c3d6ab1c.png",V="/MyWeb/assets/image-20230327204420928.88dba18e.png",J=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"note/TS/现代管理基础Note.md","filePath":"note/TS/现代管理基础Note.md"}'),v={name:"note/TS/现代管理基础Note.md"},B=o('<h2 id="现代管理基础笔记" tabindex="-1">现代管理基础笔记 <a class="header-anchor" href="#现代管理基础笔记" aria-label="Permalink to &quot;现代管理基础笔记&quot;">​</a></h2><h2 id="lecture-1" tabindex="-1">Lecture 1 <a class="header-anchor" href="#lecture-1" aria-label="Permalink to &quot;Lecture 1&quot;">​</a></h2><p><img src="'+a+'" alt="image-20230227185221968"></p><h3 id="过程考核" tabindex="-1">过程考核： <a class="header-anchor" href="#过程考核" aria-label="Permalink to &quot;过程考核：&quot;">​</a></h3><ul><li>线上知识点测试：40pts</li><li>线上个人作业及互评：10pts <ul><li>800字心得体会</li><li>计划管理 <ul><li>周计划</li><li>落实</li><li>三点体会</li></ul></li></ul></li><li>线下小组作业：30pts <ul><li>身上的管理问题</li><li>PPT</li><li>组队方式？</li><li>最后一周分享</li></ul></li><li>课上发言+随机提问：10pts <ul><li>课上发言</li><li>网上发言</li></ul></li></ul><p><img src="'+e+'" alt="image-20230227210453448"></p><p><img src="'+s+'" alt="image-20230227210623290"></p><p><strong>注意上课时间为晚上</strong></p><h3 id="现代管理的特征" tabindex="-1">现代管理的特征 <a class="header-anchor" href="#现代管理的特征" aria-label="Permalink to &quot;现代管理的特征&quot;">​</a></h3><p><img src="'+r+'" alt="image-20230227192242999"></p><ol><li><p>结构化的分析：</p><ul><li><p>基本信息</p></li><li><p>行为信息</p></li><li><p>信用信息</p></li><li><p>需求信息</p></li><li><p>...</p></li></ul></li><li><p>可视化的呈现</p></li><li><p>目的</p><ul><li>客制化服务</li></ul></li></ol><h4 id="管理环境发生变化" tabindex="-1">管理环境发生变化 <a class="header-anchor" href="#管理环境发生变化" aria-label="Permalink to &quot;管理环境发生变化&quot;">​</a></h4><ul><li>黑天鹅：不可预知的未来事件</li><li>灰犀牛：大概率发生的恶性事件</li></ul><h4 id="时代背景" tabindex="-1"><strong>时代背景</strong> <a class="header-anchor" href="#时代背景" aria-label="Permalink to &quot;**时代背景**&quot;">​</a></h4><ul><li>VUCA(乌卡)时代 <ul><li>变动的</li><li>不确定的</li><li>复杂的</li><li>模糊的</li></ul></li><li>RUPT时代 <ul><li>急速的</li><li>莫测的</li><li>矛盾的</li><li>复杂的</li></ul></li></ul><h4 id="管层面的新思考" tabindex="-1"><strong>管层面的新思考</strong> <a class="header-anchor" href="#管层面的新思考" aria-label="Permalink to &quot;**管层面的新思考**&quot;">​</a></h4><ul><li>环境洞察：Environment Insight</li><li>情景规划：Scenario Planning <ul><li>对不同情景的方案</li></ul></li><li>快速反应：Rapid Response</li><li>组织弹性：Company Flexibility <ul><li>组织韧性</li></ul></li><li>跨界合作：Cross-border Cooperation</li><li>心智锤炼：Mental Training</li><li>舆情管控：Public opinion control</li><li>生态圈层</li><li>有效复盘</li></ul><p>=&gt; 十倍速变化</p><p><strong>管理对象</strong></p><ul><li>自我管理：self-management <ul><li>情绪</li><li>物品</li><li>时间</li><li>空间</li><li>...</li></ul></li><li>团队管理</li><li>组织管理</li><li>社会管理</li><li>生态管理</li></ul><p><strong>管理过程</strong>：4+1</p><ul><li>计划</li><li>组织：人、财、物</li><li>领导：领导的对象是人，要加以控制</li><li>控制：把握、落实、施行标准</li><li>创新 <ul><li>迭代</li><li>颠覆</li></ul></li></ul><p><strong>管理者</strong></p><ul><li>素质要求</li><li>基本职责</li><li>发展通道</li></ul><p><img src="'+p+'" alt="image-20230227205501935"></p><p>管理要回答的三个问题：</p><ul><li><p>目标</p></li><li><p>路径</p><ul><li>多个备选方案</li></ul></li><li><p>“施工”方案</p><ul><li>领导</li><li>控制</li></ul></li></ul><p><a href="https://www.amazon.cn/dp/B085C9Q31P" target="_blank" rel="noreferrer">https://www.amazon.cn/dp/B085C9Q31P</a></p><p><img src="https://images-cn.ssl-images-amazon.cn/images/I/31Ik4Mcdb8L.jpg" alt="“现代管理基础（第二版）”，作者：[周亚庆]"></p><p><img src="'+n+'" alt="image-20230306185359524"></p><h2 id="lecture-2-管理及其有效管理chapter-1" tabindex="-1">Lecture 2：管理及其有效管理chapter 1 <a class="header-anchor" href="#lecture-2-管理及其有效管理chapter-1" aria-label="Permalink to &quot;Lecture 2：管理及其有效管理chapter 1&quot;">​</a></h2><h3 id="chapter-1-管理导论" tabindex="-1">chapter 1 管理导论 <a class="header-anchor" href="#chapter-1-管理导论" aria-label="Permalink to &quot;chapter 1 管理导论&quot;">​</a></h3><h4 id="第一节-管理的内涵" tabindex="-1">第一节 管理的内涵 <a class="header-anchor" href="#第一节-管理的内涵" aria-label="Permalink to &quot;第一节 管理的内涵&quot;">​</a></h4><ul><li><p><strong>传统定义</strong></p><ol><li>德鲁克（现代管理之父）：管理是<strong>谋取剩余</strong>（<em>产出大于投入的部分</em>）</li></ol><blockquote><p><strong>精益管理</strong>：旨在需要的时候，按需要的量，生产所需的产品。</p><ul><li>库存控制</li><li>生产计划</li><li>成本管理</li><li>供应链</li><li>...</li></ul><p><strong>品牌依恋/品牌忠诚</strong></p><p><strong>商业模式</strong></p></blockquote><ol start="2"><li>西蒙（决策管理大师）：管理就是<strong>决策</strong></li></ol><blockquote><p>简单决策/复杂决策</p><p>个人决策/群体决策</p></blockquote><ol start="3"><li>穆尼：管理就是<strong>领导</strong></li></ol><blockquote><p>关注领导的<strong>有效性</strong></p><p>领导的面向是人，管理的面向可以是其他东西</p><p>领导往往与重大事项联系在一起，管理依托职位的权力</p></blockquote><ol start="4"><li>法约尔（经营管理之父）：管理就是计划、组织、指挥、协调和<strong>控制</strong></li></ol><blockquote><p><strong>多角度思考：立场、方法、角度不一样</strong></p><p>在一定环境下对组织内外资源包括人力资源的调配</p></blockquote><ol start="5"><li>孔茨：管理是借助他人达成目标的过程</li></ol></li><li><p><strong>本书定义：</strong></p><p>​ 管理是指人们根据组织的内外部<strong>环境</strong>，整合人、财、物、信息和时间等<strong>资源</strong>，通过==<em><strong>计划、组织、领导、控制与创新</strong></em>==等<strong>职能</strong>，以有效<strong>实现预定目标</strong>的过程。</p></li><li><p><strong>管理要点：</strong></p><ol><li><strong>管理的工作</strong>必须基于特定环境。管理工作需要不断适应环境。</li><li><strong>管理的对象</strong>是组织的资源。<em><strong>人</strong>是生产力中最根本的、最活跃的因素</em>，人具有主观能动性。</li><li><strong>管理的基本职能</strong>是<em>计划、组织、领导、控制与创新</em>。</li><li><strong>管理的核心</strong>是<strong>协调与整合</strong>。</li><li><strong>管理的目标</strong>是高效地实现预定<strong>目标</strong>。</li></ol><blockquote><p><strong>美好的愿景</strong>是团队合作的基石；</p><p><strong>明确的目标</strong>是团队成功的关键；</p><p><strong>团结协作</strong>是团队合作的关键。</p><p>经验也是一种生产力，通过在团体中的共享，可以产生意想不到的效果。</p></blockquote></li><li><p><strong>管理的实质</strong></p><p><strong>协商是管理的本质</strong>，管理是手段而不是目的。人类活动的显著特征之一就是具有明确的目的性。</p><ul><li>管理本身不是目的</li><li>管理是一种工具</li></ul></li></ul><h4 id="第二节-管理思想的演变" tabindex="-1">第二节 管理思想的演变 <a class="header-anchor" href="#第二节-管理思想的演变" aria-label="Permalink to &quot;第二节 管理思想的演变&quot;">​</a></h4><blockquote><p>管理考虑：管理者感受、管理成本</p></blockquote><ul><li>管理产生的原因</li></ul><p><img src="'+g+'" alt="image-20230306192527448"></p><p>资源的有限性：人员、时间、媒介、空间...</p><p><strong>=&gt;资源有限性与欲望无限性的矛盾普遍存在</strong></p><ol><li>生产：通过<strong>劳动</strong>向大自然索要资源，从而在一定程度上满足自身欲望。</li><li>贸易：通过自身多余或价值不大的资源与他人<strong>交换</strong>，获得自身稀缺的资源，以更好地满足自身的欲望。</li><li>组织：通过与他人的<strong>分工协助</strong>，形成超越个人的群体力量，从而达到靠个人的力量无法实现的欲望。</li><li>道德：通过<strong>教育约束</strong>每一个人的欲望，使有限的资源足以满足众人的欲望。</li><li>战争：通过<strong>掠夺他人资源</strong>来扩展自身资源，从而满足自己的欲望。</li><li>管理：通过<strong>科学</strong>的方法提高资源的利用率，力求以有限的资源实现尽可能多的欲望。</li></ol><ul><li><p>管理思想的发展</p><ol><li><p>早期管理</p></li><li><p>经验管理</p></li><li><p>科学管理</p><ol><li><p>科学管理理论</p><p>四条原则</p><table><thead><tr><th>原则</th><th>内容</th></tr></thead><tbody><tr><td>科学的方法</td><td>对工作的每个要素开发出科学的方法，以代替陈旧的经验方法</td></tr><tr><td>合适的工人</td><td>科学地挑选，培训与教导工人，使之成长</td></tr><tr><td>真诚的合作</td><td>管理者真诚与工人进行合作，以确保工人按照科学的方法形式</td></tr><tr><td>明确的职责</td><td>管理者：按照科学原理指定工作方法 ；工人负责按此完成相应的工作</td></tr></tbody></table><p>科学管理是所有相关要素的整合</p></li><li><p>一般管理理论</p></li><li><p>官僚组织理论</p></li></ol></li><li><p>行为管理思想</p><ul><li><p>将重点放在影响组织中个体行为的各种因素上，强调管理的重点是理解人的行为</p></li><li><p>基本观点：人不仅是经济人，更是社会人，其劳动生产率受社会、心理和群体因素影响</p></li></ul><ol><li>霍桑实验 <ul><li>工人是“社会人”，是复杂的社会系统成员，人们的行为不单纯出于追求金钱的动机，还有社会、心理方面的需求。领导者若能真诚关心下属，重视感情沟通，那么工人的生产率将会有较大的提高。</li><li>企业在除了正式组织还存在非正式组织。非正式组织以其特有的感情倾向与精神导向，左右着成员的行为。</li><li>新的领导能力在于提高工人的满意度。工人的高满意度来源于其个人需求的有效满足，既包括物质需求，也包括精神需求。</li></ul></li><li>人际关系运动 <ul><li>目的是提高人际技能与克服沟通障碍。</li></ul></li><li>行为科学学派 <ul><li>强调通过科学研究来形成关于组织中的人的行为理论，并据此指导管理者的管理实践。</li></ul></li></ol></li><li><p>定量管理思想</p><ul><li>有效管理的关键在于度的把握，定量化能提高管理的效率与效益。定量管理思想的核心是把数学、统计学和信息技术用于管理决策与提高。</li></ul><ol><li>管理科学</li><li>作业管理</li><li>信息技术在管理中的应用</li></ol></li><li><p>权变管理思想</p><ol><li>系统理论 <ul><li>管理者的作用就是确保组织中的各个部分之间的相互协调，以实现组织的整体目标。</li></ul></li><li>权变理论 <ul><li>组织的管理应根据其所处在内外部环境的变化而变化。</li></ul></li><li>过程理论 <ul><li>管理是一个过程，认为管理由许多相互关联的职能所组成</li></ul></li></ol></li></ol></li></ul><h4 id="第三节-管理的重要性" tabindex="-1">第三节：管理的重要性 <a class="header-anchor" href="#第三节-管理的重要性" aria-label="Permalink to &quot;第三节：管理的重要性&quot;">​</a></h4><h4 id="第四节-管理学及其特点" tabindex="-1">第四节：管理学及其特点 <a class="header-anchor" href="#第四节-管理学及其特点" aria-label="Permalink to &quot;第四节：管理学及其特点&quot;">​</a></h4><p>管理的特点</p><ul><li>管理学是一门不精确的科学</li><li>管理学是一门综合性的科学</li><li>管理学是一门应用性的科学</li></ul><p>管理学是一门科学，管理实践则具有艺术性。管理的精髓是“理”不是“管”</p><h2 id="lecture-3-有效管理" tabindex="-1">Lecture 3 有效管理 <a class="header-anchor" href="#lecture-3-有效管理" aria-label="Permalink to &quot;Lecture 3 有效管理&quot;">​</a></h2><p><img src="'+u+'" alt="image-20230313190513192"></p><p><img src="'+c+'" alt="image-20230313190605095"></p><p>全面理解管理：</p><ul><li><p>管理必须基于特定的<strong>环境</strong></p></li><li><p>管理的<strong>对象</strong>是组织的<strong>资源</strong>（包括人力资源）</p></li><li><p>管理的基本职能是<strong>计划、组织、领导、控制和创新</strong></p></li><li><p>管理的核心是<strong>协调</strong>和<strong>整合</strong></p><ul><li>协调：使多个看似矛盾的事物（如长远目标与短期目标、有限资源和无限欲望）之间的有机结合、同步和谐</li><li>形式： <ul><li>协调工作中的问题</li><li>协调工作间的问题</li><li>协调活动与目标之间的关系</li></ul></li></ul></li><li><p>管理的目的是<strong>有效实现既定目标</strong></p></li><li><p>管理的实质是人们为了<strong>实现一定的目标</strong>而采取的一种手段</p></li><li><p>管理的功能是以<strong>有限的资源</strong>实现<strong>更多的目标</strong></p></li><li><p>管理的道德</p><ul><li>商业向善</li></ul></li></ul><p>管理的基础和依据：法理/道理/情理</p><p>管理的方法：整理/治理/调理</p><p>管理的结果：合理/有理</p><p><strong>管理好坏如何区分和衡量</strong></p><ul><li>投入产出比</li><li>可持续发展</li><li>目标达成</li><li>文化</li><li>流动性</li><li>规模</li><li>客户满意度/客户留存率</li><li>员工满意度/领导满意度</li></ul><h3 id="衡量管理有效性" tabindex="-1">衡量管理有效性 <a class="header-anchor" href="#衡量管理有效性" aria-label="Permalink to &quot;衡量管理有效性&quot;">​</a></h3><p>有效性指标：</p><ul><li><strong>效率：投入产出比</strong>=&gt; 怎么做：以比较经济的方法</li><li><strong>效益：目标达成度</strong>=&gt; 做什么：做有利于<strong>目标</strong>实现的事情</li></ul><p><em>有效的管理：<strong>做好对的事</strong>，既要讲究效率又要讲究效益</em> To do right thing right</p><p><strong>效益是优先的。其次再考虑方法。</strong></p><p>德鲁克：有效管理的五项原则</p><ol><li>知道时间用在哪里</li><li>重视外部贡献</li><li>善于利用各人的长处</li><li>集中精力于重要领域</li><li>善于做出有效的决策</li></ol><img src="'+m+'" alt="image-20230313203307160" style="zoom:50%;"><blockquote><p>创新和决策贯穿管理的全过程</p></blockquote><ul><li><p>计划：</p><ul><li>计划是管理的首要职责</li><li><strong>解决合理配置资源的问题</strong></li><li>有效计划特征： <ul><li>目的性</li><li>预见性</li><li>稳定性：相对稳定</li><li>灵活性</li></ul></li><li></li></ul></li><li><p>组织</p><ul><li>组织工作是为了有效达成计划所确定的目标而进行<strong>组织结构的设计</strong>与变革、<strong>人员配备</strong>以及<strong>权力配置</strong>的过程</li><li>关注内外资源的整合利用：比如不同领域间企业的<em>合作</em></li><li>要求： <ul><li>合理性</li><li>效益性</li><li>规范性</li><li>动态性</li></ul></li></ul></li><li><p>领导</p><ul><li><strong>权力配置</strong></li><li>领导职能更受<strong>管理者素质和管理风格</strong>的影响，是一件具有很强<strong>艺术性</strong>的管理活动。</li></ul><img src="'+h+'" alt="image-20230313205806923" style="zoom:50%;"></li><li><p>控制</p><ul><li>检查、评估、纠正</li><li>定标准、测成效、纠偏差</li><li>注意沟通</li></ul></li><li><p>创新</p><ul><li>五种创新 <ul><li>采用一种新产品</li><li>采用一种新生产方法</li><li>开辟一个新市场</li><li>活动原材料或半成品的一种新供应来源</li><li>实现任何一种产业的新组织</li></ul></li></ul></li></ul><img src="'+d+'" alt="image-20230313211254362" style="zoom:50%;"><img src="'+b+'" alt="image-20230313211311946" style="zoom:50%;"><img src="'+_+'" alt="image-20230313211346807" style="zoom:50%;"><h2 id="lecture-4" tabindex="-1">Lecture 4 <a class="header-anchor" href="#lecture-4" aria-label="Permalink to &quot;Lecture 4&quot;">​</a></h2><blockquote><p>现代奥运三原则：排他性、垄断性、赞助商</p></blockquote><img src="'+q+'" alt="image-20230320194731091" style="zoom:50%;"><img src="'+f+'" alt="image-20230320194846753" style="zoom:50%;"><p>思维定势：</p><ul><li>本本定式</li><li>权威定式</li><li>从众定式</li><li>经验定式</li></ul><h3 id="奥斯本核验表" tabindex="-1">奥斯本核验表 <a class="header-anchor" href="#奥斯本核验表" aria-label="Permalink to &quot;奥斯本核验表&quot;">​</a></h3><p><img src="'+k+'" alt="image-20230320195524123"></p><p><strong>能否他用：</strong></p><ul><li>现有事物有无他用</li><li>保持不变能否扩大用途</li><li>稍加改变有无其他用途</li></ul><hr><ul><li>思路转化</li><li>原理转化</li><li>应用转化</li><li>功能转化</li><li>材料转化</li></ul><p><strong>能否借用：</strong></p><p><strong>能否改变：</strong></p><p>能否缩小：</p><ul><li>现有事物能否减少、缩小或省略某些部分</li><li>能否浓缩化</li><li>能否微型化</li><li>能否短、轻、压缩、分割、简略</li><li>原料代用</li></ul><p>能否代用：</p><ul><li>其他原理、方法、工艺</li><li>能否使用其他结构、动力、设备</li></ul><img src="'+y+'" alt="image-20230320201922023" style="zoom:50%;"><img src="'+P+'" alt="image-20230320202319069" style="zoom:50%;"><h3 id="第三章" tabindex="-1">第三章 <a class="header-anchor" href="#第三章" aria-label="Permalink to &quot;第三章&quot;">​</a></h3><ul><li>区分企业和组织</li><li>企业存在的条件</li><li>企业存在价值</li><li>企业类型</li><li><strong>企业生命周期</strong></li><li><strong>企业发展的影响因素</strong></li></ul><h4 id="组织" tabindex="-1">组织 <a class="header-anchor" href="#组织" aria-label="Permalink to &quot;组织&quot;">​</a></h4><ul><li>营利性</li><li>非营利性</li></ul><p>企业是人的要素和物的要素的组合，自主经验、自负盈亏、独立核算</p><p>根据经济活动方式分类：生产型、流通型、服务型</p><p>根据生成要素密集程度：劳动密集型、资金密集型、知识密集型</p><p>根据生成规模：</p><p>根据国民经济部分：</p><p>根据财产组织形式：</p><ul><li>个人独资企业 <ul><li>由一个自然人投资，全部资产为投资人个人所有</li><li>注册门槛低、注册费用低、操作简单、<strong>税种简单</strong></li></ul></li><li>合伙制企业</li><li>公司制企业 <ul><li>责任有限公司</li><li>股份有限工资</li></ul></li></ul><h4 id="企业生命周期" tabindex="-1">企业生命周期 <a class="header-anchor" href="#企业生命周期" aria-label="Permalink to &quot;企业生命周期&quot;">​</a></h4><ul><li><p>孕育期和婴儿期：<strong>创业阶段</strong></p><ul><li>产品创新</li><li>营销创新</li></ul></li><li><p>学步期和青春期：<strong>成长阶段</strong></p><ul><li>规范管理</li><li>有效扩张</li></ul></li><li><p>盛年期和稳定器：<strong>成熟阶段</strong></p><ul><li><p>全面创新</p></li><li><p>活化创新</p><blockquote><p>企业管理是为了延展成熟期，当老化期来临时可以找到第二曲线。</p></blockquote></li></ul></li><li><p>贵族期、官僚期、死亡期：<strong>老化阶段</strong></p><ul><li>增加危机意识</li><li>注重企业再造</li></ul></li></ul><h4 id="生命周期要素的分析" tabindex="-1">生命周期要素的分析 <a class="header-anchor" href="#生命周期要素的分析" aria-label="Permalink to &quot;生命周期要素的分析&quot;">​</a></h4><ul><li><p>P执行功能</p></li><li><p>A行政功能</p></li><li><p>E创新功能</p></li><li><p>I整合功能</p></li></ul><p>企业是一种扩大了概念内涵的“人”：</p><ul><li>人性企业</li><li>复杂环境</li><li>总会生病</li></ul><h4 id="企业发展的环境" tabindex="-1">企业发展的环境 <a class="header-anchor" href="#企业发展的环境" aria-label="Permalink to &quot;企业发展的环境&quot;">​</a></h4><ul><li>宏观环境</li><li>微观环境</li></ul><blockquote><p>宏观环境难以改变，微观环境可以影响</p></blockquote><hr><ul><li>内部环境 <ul><li>直接影响：<strong>经营条件因素</strong><ul><li>人力资源、资金、品牌</li></ul></li><li>间接影响：<strong>组织文化因素</strong></li></ul></li><li>外部环境 <ul><li>直接影响：<strong>任务环境因素</strong><ul><li>供应商、竞争者、顾客、主管部门</li></ul></li><li>间接影响：<strong>一般环境因素</strong><ul><li>政治、经济、社会、技术</li></ul></li></ul></li></ul><p><strong>环境特征：</strong></p><img src="'+x+'" alt="image-20230320210344918" style="zoom:50%;"><p>动荡而复杂的环境</p><ul><li>组织结构扁平化</li><li>削减中间层</li><li>增加对外部环境的敏感性</li></ul><h4 id="关注企业文化" tabindex="-1">关注企业文化 <a class="header-anchor" href="#关注企业文化" aria-label="Permalink to &quot;关注企业文化&quot;">​</a></h4><p>对内 : 企业文化&lt;=&gt;品牌文化 : 对外</p><p>精神、制度、物质</p><img src="'+M+'" alt="image-20230320211521261" style="zoom:50%;"><h2 id="lecture-5-决策" tabindex="-1">Lecture 5 决策 <a class="header-anchor" href="#lecture-5-决策" aria-label="Permalink to &quot;Lecture 5 决策&quot;">​</a></h2><p><img src="'+W+'" alt="image-20230327190512925"><img src="'+z+'" alt="image-20230327190541921"><img src="'+T+'" alt="image-20230327190623061"><img src="'+L+'" alt="image-20230327190342005"></p><p><strong>价值：</strong></p><ul><li>成本（投入）</li><li>效用（回报）</li></ul><p><strong>顾客让渡价值理论：</strong></p><blockquote><p>顾客让渡价值（Customer Delivered Value）：<strong>顾客让渡价值理论</strong>是从消费者的实际出发来研 究消费者在购买过程中的心理感受，寻求顾客的满意。 顾客让渡价值是指总顾客价值与总顾 客成本之差，即顾客让渡价值=总顾客价值-总顾客成本，消费者总是选择购买给他们带来较 高顾客让渡价值的产品。</p><p>成本：</p><ul><li>时间</li><li>货币</li><li>体力</li><li>精力（心理成本，比如安全因素，心理压力）</li></ul><p>效用：</p><ul><li>实用</li><li>象征</li></ul></blockquote><h3 id="决策概念" tabindex="-1">决策概念 <a class="header-anchor" href="#决策概念" aria-label="Permalink to &quot;决策概念&quot;">​</a></h3><p>为了达到某一目的而从若干可行方案中<strong>选择</strong>一个满意方案的分析判断<strong>过程</strong></p><p>是管理者识别并解决问题的过曾，是利用机会的过程。</p><p><em><strong>决策贯穿管理的全过程</strong></em></p><h3 id="决策类型" tabindex="-1">决策类型 <a class="header-anchor" href="#决策类型" aria-label="Permalink to &quot;决策类型&quot;">​</a></h3><ol><li><p>时间</p><ul><li><p>长期决策（战略决策）</p></li><li><p>短期决策（战术决策）</p></li></ul></li><li><p>主体</p><ul><li><p>集体决策</p></li><li><p>个人决策</p><p>集体决策不是集体违规，集体决策不能成为个人意志的附庸</p></li></ul></li><li><p>起始点</p><ul><li>初始决策</li><li>追踪决策：随着初始决策的实施，组织环境发生变化而进行的决策</li></ul></li><li><p>重复性</p><ul><li>程序化决策：例行决策</li><li>非程序化决策：例外决策</li></ul></li><li><p>风险</p><ul><li>确定型决策</li><li>风险型决策</li><li>不确定型决策</li></ul></li><li><p>重要程度</p><ul><li>战略攻略</li><li>管理决策</li><li>业务决策</li></ul></li></ol><h3 id="科学决策过程" tabindex="-1">科学决策过程 <a class="header-anchor" href="#科学决策过程" aria-label="Permalink to &quot;科学决策过程&quot;">​</a></h3><p><img src="'+C+'" alt="image-20230327201730639"></p><ol><li>备择方案：越多越好</li></ol><blockquote><p>如果你感到似乎只有一条路可走，那可能这条路是走不通的。</p><p>所谓决策民主，执行决裁。</p></blockquote><ol start="2"><li><p>分析和比较方案：考虑超前性和可行性</p></li><li><p>满意代替最优</p></li><li><p>辩证看待限制性因素</p></li><li><p>动态修订</p></li><li><p>综合比较</p><ol><li>保守（损失敏感）/进取（利益敏感）/稳妥（关注机会损失）</li><li>理性+感性</li><li>数据+直觉</li></ol></li></ol><p><img src="'+S+'" alt="image-20230327204335171"></p><p><img src="'+N+'" alt="image-20230327194719243"></p><p>需要找到问题的<strong>choke points</strong>，进行正确的<strong>定位</strong>。</p><h3 id="金字塔-逻辑树" tabindex="-1">金字塔/逻辑树 <a class="header-anchor" href="#金字塔-逻辑树" aria-label="Permalink to &quot;金字塔/逻辑树&quot;">​</a></h3><p><img src="'+w+'" alt="image-20230327200844740"><img src="'+I+'" alt="image-20230327201402661"></p><p><strong>目的：从一个被粗略界定的复杂问题到一个完整内在联系的一系列相关议题。</strong></p><p><img src="'+A+'" alt="image-20230327201608482"></p><h3 id="决策的影响因素" tabindex="-1">决策的影响因素 <a class="header-anchor" href="#决策的影响因素" aria-label="Permalink to &quot;决策的影响因素&quot;">​</a></h3><p><img src="'+V+'" alt="image-20230327204420928"></p><h3 id="决策技巧" tabindex="-1">决策技巧 <a class="header-anchor" href="#决策技巧" aria-label="Permalink to &quot;决策技巧&quot;">​</a></h3><ul><li>头脑风暴</li><li>专家群体决策</li><li>名义群体决策</li><li>发散思维</li></ul>',148),E=[B];function R($,D,O,Q,U,j){return i(),t("div",null,E)}const G=l(v,[["render",R]]);export{J as __pageData,G as default};
